

SET SQL_MODE = "NO_AUTO_VALUE_ON_ZERO";
START TRANSACTION;
SET time_zone = "+00:00";


CREATE TABLE `members` (
  `num` int(11) NOT NULL,
  `id` char(15) NOT NULL,
  `pass` char(15) NOT NULL,
  `name` char(10) NOT NULL,
  `tel` char(20) NOT NULL,
  `email` char(80) NOT NULL,
  `animal_name` char(30) DEFAULT NULL,
  `animal_sort` char(20) DEFAULT NULL,
  `regist_day` char(20) DEFAULT NULL,
  `level` int(11) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;




CREATE TABLE `product` (
  `num` int(11) NOT NULL,
  `name` char(50) NOT NULL,
  `price` int(11) NOT NULL,
  `dis_price` int(11) DEFAULT NULL,
  `sum_introduce` char(150) NOT NULL,
  `brand` char(100) DEFAULT NULL,
  `recommended_time` char(100) DEFAULT NULL,
  `link` char(255) DEFAULT NULL,
  `event` char(50) DEFAULT NULL,
  `animal` char(20) DEFAULT NULL,
  `category` char(20) DEFAULT NULL,
  `category_f` char(20) DEFAULT NULL,
  `introduce` char(255) DEFAULT NULL,
  `information` char(255) DEFAULT NULL,
  `component_a` char(20) DEFAULT NULL,
  `component_b` char(20) DEFAULT NULL,
  `component_c` char(20) DEFAULT NULL,
  `component_d` char(20) DEFAULT NULL,
  `component_e` char(20) DEFAULT NULL,
  `component_f` char(20) DEFAULT NULL,
  `component_g` char(20) DEFAULT NULL,
  `daily_recommend` char(80) DEFAULT NULL,
  `way` char(255) DEFAULT NULL,
  `precautions` char(255) DEFAULT NULL,
  `regist_day` char(20) NOT NULL,
  `hit` int(11) NOT NULL,
  `file_name` char(40) DEFAULT NULL,
  `file_type` char(40) DEFAULT NULL,
  `file_copied` char(40) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;



