  `title` char(80) NOT NULL,
  `content` char(255) NOT NULL,
  `regist_day` char(20) DEFAULT NULL,
  `answer` char(255) DEFAULT NULL,
  `regist_day_a` char(20) DEFAULT NULL,
  `level` int(11) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;




ALTER TABLE `members`
  ADD PRIMARY KEY (`num`);

ALTER TABLE `product`
  ADD PRIMARY KEY (`num`);


